INSERT INTO `siem_law_rules` VALUES
(1,'Bộ tiêu chí CNTT BYT','R-AUD-07','Lưu log định dạng chuẩn JSON','allowed','TT 54/2017; Bộ tiêu chí CNTT BYT','Dễ truy vấn, dễ phân tích.',NULL,'[\"json_valid=true\", \"schema_version\", \"parser_status\"]','[\"Validate JSON schema.\"]','PHẦN IV – GHI VẾT, KIỂM TOÁN & GIÁM SÁT (AUDIT & LOGGING)','USER','2025-11-21 15:03:15','2025-12-20 15:38:17','Phạt tiền 10-30 triệu đồng (cá nhân); 20-60 triệu đồng (tổ chức) theo NĐ 15/2020/NĐ-CP Điều 100. Vi phạm nghiêm trọng: 50-100 triệu đồng.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-15-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-buu-chinh-vien-thong-tan-so-vo-tuyen-dien-350499.aspx'),
(2,'Bộ tiêu chí CNTT BYT','R-AUD-18','Mỗi truy cập API phải có token định danh','required','TT 54/2017; Bộ tiêu chí CNTT','Định danh truy cập hệ thống.',NULL,'[\"api_endpoint\", \"authorization_token\", \"token_issuer\", \"expiry\"]','[\"Kiß╗âm tra header Authorization.\"]','PHẦN IV – GHI VẾT, KIỂM TOÁN & GIÁM SÁT (AUDIT & LOGGING)','USER','2025-11-21 15:03:15','2025-12-20 15:38:17','Phạt tiền 10-30 triệu đồng (cá nhân); 20-60 triệu đồng (tổ chức) theo NĐ 15/2020/NĐ-CP Điều 100. Vi phạm nghiêm trọng: 50-100 triệu đồng.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-15-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-buu-chinh-vien-thong-tan-so-vo-tuyen-dien-350499.aspx'),
(3,'Bộ tiêu chí CNTT BYT','R-CON-17','Phải ghi rõ cơ chế truyền dữ liệu (API/FHIR/HL7)','allowed','TT 46/2018; Bộ tiêu chí CNTT BYT','Chuẩn trao đổi liên thông.',NULL,'[\"transfer_mechanism=API\"]','[\"FHIR\"]','PHẦN VI – ĐỒNG THUẬN & CHIA SẺ THÔNG TIN (CONSENT & DATA DISCLOSURE)','USER','2025-11-21 15:03:15','2025-12-18 09:37:10','Phạt tiền 50-100 triệu đồng (cá nhân); 100-200 triệu đồng (tổ chức) theo NĐ 13/2023/NĐ-CP. Mua bán dữ liệu: tới 3 tỷ đồng hoặc 10 lần khoản thu vi phạm.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-13-2023-ND-CP-bao-ve-du-lieu-ca-nhan-555642.aspx'),
(4,'Bộ tiêu chí CNTT BYT','R-DAM-17','Chỉ cho phép API FHIR/HL7','allowed','TT 46/2018; Bộ tiêu chí CNTT BYT','Chuẩn hóa trao đổi.',NULL,'[\"api_name\", \"api_schema=FHIR\"]','[\"HL7v2\", \"resource_type/id\", \"validation_result\"]','PHẦN III – QUẢN LÝ TRUY CẬP & DỮ LIỆU (DATA ACCESS MANAGEMENT)','USER','2025-11-21 15:03:15','2025-12-18 09:37:10','Phạt tiền 30-70 triệu đồng (cá nhân); 60-140 triệu đồng (tổ chức) theo NĐ 117/2020/NĐ-CP. Xử lý dữ liệu y tế trái phép: tới 5% doanh thu.','https://thuvienphapluat.vn/van-ban/The-thao-Y-te/Nghi-dinh-117-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-y-te-426783.aspx'),
(5,'Bộ tiêu chí CNTT BYT','R-INT-14','Đồng bộ theo cơ chế publish-subscribe an toàn','allowed','Bộ tiêu chí CNTT BYT','Giảm tải truy vấn đồng bộ.',NULL,'[\"broker_type=Kafka\"]','[\"MQTT\", \"topic_name\", \"qos\", \"auth_flag\"]','PHẦN VIII – LIÊN THÔNG & CHIA SẺ KỸ THUẬT (INTEROPERABILITY & TECHNICAL EXCHANGE)','USER','2025-11-21 15:03:15','2025-12-29 10:03:09','Phạt tiền 10-30 triệu đồng (cá nhân); 20-60 triệu đồng (tổ chức) theo NĐ 15/2020/NĐ-CP. Chuyển dữ liệu y tế không đúng chuẩn: cảnh cáo + buộc khắc phục.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-15-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-buu-chinh-vien-thong-tan-so-vo-tuyen-dien-350499.aspx'),
(6,'Bộ tiêu chí CNTT BYT','R-SIG-18','Cấm xóa hồ sơ đã ký và gửi CDR','not_allowed','TT 46/2018; Bộ tiêu chí CNTT BYT','Hồ sơ lưu chính thức.',NULL,'[\"status=archived\", \"event_type!=DELETE\"]','[\"Block delete nß║┐u archived=true.\"]','PHẦN V – CHỮ KÝ SỐ & TOÀN VẸN HỒ SƠ (DIGITAL SIGNATURE & INTEGRITY)','USER','2025-11-21 15:03:15','2025-12-20 15:38:17','Phạt tiền 20-50 triệu đồng (cá nhân); 40-100 triệu đồng (tổ chức) theo NĐ 15/2020/NĐ-CP. Giả mạo chữ ký số: xử lý hình sự theo Điều 341 BLHS.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-15-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-buu-chinh-vien-thong-tan-so-vo-tuyen-dien-350499.aspx'),
(7,'HL7 FHIR','R-INT-17','Phải ghi Resource Type khi liên thông FHIR','required','TT 46/2018; HL7 FHIR','Giúp truy xuất cụ thể.',NULL,'[\"resource_type\", \"resource_id\", \"interaction_type\"]','[\"Validate FHIR schema.\"]','PHẦN VIII – LIÊN THÔNG & CHIA SẺ KỸ THUẬT (INTEROPERABILITY & TECHNICAL EXCHANGE)','USER','2025-11-21 15:03:15','2025-12-29 10:03:09','Phạt tiền 10-30 triệu đồng (cá nhân); 20-60 triệu đồng (tổ chức) theo NĐ 15/2020/NĐ-CP. Chuyển dữ liệu y tế không đúng chuẩn: cảnh cáo + buộc khắc phục.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-15-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-buu-chinh-vien-thong-tan-so-vo-tuyen-dien-350499.aspx'),
(8,'HL7 FHIR','R-INT-19','Phải hỗ trợ API truy vấn theo patient_id','required','TT 46/2018; HL7 FHIR','Cho phép chia sẻ liên viện.',NULL,'[\"endpoint=/Patient/{id}\", \"response_status\", \"latency\"]','[\"Test query valid.\"]','PHẦN VIII – LIÊN THÔNG & CHIA SẺ KỸ THUẬT (INTEROPERABILITY & TECHNICAL EXCHANGE)','USER','2025-11-21 15:03:15','2025-12-29 10:03:09','Phạt tiền 10-30 triệu đồng (cá nhân); 20-60 triệu đồng (tổ chức) theo NĐ 15/2020/NĐ-CP. Chuyển dữ liệu y tế không đúng chuẩn: cảnh cáo + buộc khắc phục.','https://thuvienphapluat.vn/van-ban/Cong-nghe-thong-tin/Nghi-dinh-15-2020-ND-CP-xu-phat-vi-pham-hanh-chinh-linh-vuc-buu-chinh-vien-thong-tan-so-vo-tuyen-dien-350499.aspx'),
